const teambuilder=require('../js/ak.js');let assert=require('assert');describe('CeasarCodeDecoder',function(){describe('decrypt',function(){it('should return the correct number',function(){let a='ku oø voa xyu',b=teambuilder.AK(a);assert.equal(b.decrypt(),'ak er let nok')})}),describe('encrypt',function(){it('should return the correct number',function(){let a='ak er let nok',b=teambuilder.AK(a);assert.equal(b.encrypt(),'ku oø voa xyu')})})})