const teambuilder=require('../js/alfa.js');let assert=require('assert');describe('Alfa',function(){describe('decrypt',function(){it('should return the correct number',function(){let a='virker ikke',b=teambuilder.Alfa(a);assert.equal(b.decrypt(),'virker ikke')})}),describe('encrypt',function(){it('should return the correct number',function(){let a='Jeg er alfa',b=teambuilder.Alfa(a);assert.equal(b.encrypt(),'10 5 7    5 18    1 12 6 1')})}),describe('encrypt',function(){it('should return the correct number',function(){let a='Jeg er alfa uden w',b=teambuilder.Alfa(a,'false');assert.equal(b.encrypt(),'10 5 7    5 18    1 12 6 1    21 4 5 14    w')})})})