const teambuilder=require('../js/backwards.js');let assert=require('assert');describe('Backwards',function(){describe('decrypt',function(){it('should return the correct number',function(){let a='virker ikke',b=teambuilder.Backwards(a);assert.equal(b.decrypt(),'virker ikke')})}),describe('encrypt',function(){it('should return the correct number',function(){let a='Jeg er bagvendt kode',b=teambuilder.Backwards(a);assert.equal(b.encrypt(),'edok tdnevgab re geJ')})}),describe('encrypt',function(){it('should return the correct number',function(){let a='Jeg er bagvendt kode',b=teambuilder.Backwards(a,'true','true');assert.equal(b.encrypt(),'edoktdnevgabregeJ')})})})