const teambuilder=require('../js/passcode.js');let assert=require('assert');describe('Passcode',function(){describe('decrypt',function(){it('should return the correct number',function(){let a='virker ikke',b=teambuilder.Passcode(a);assert.equal(b.decrypt(),'virker ikke')})}),describe('encrypt',function(){it('should return the correct number',function(){let a='Jeg er spejdkoden',b=teambuilder.Passcode(a);assert.equal(b.encrypt(),'Qoy od mnoqr√∏erop')})})})